mapper.select:
		 select 
 		  
 		  from system_user_tb systemUser
 		  where systemUser.id = #{ id }

mapper.delete:
		 delete 
 		  from system_user_tb
 		  where id = #{ id }

mapper.select:
		 select 
 		  
 		  from system_user_tb systemUser
 		  where 1=1 
 		 
 			 and systemUser.id = #{id}
 		  
 		 
 			 and systemUser.login_name = #{loginName}
 		  
 		 
 			 and systemUser.user_name = #{userName}
 		  
 		 
 			 and systemUser.password = #{password}
 		  
 		 
 			 and systemUser.phone = #{phone}
 		  
 		 
 			 and systemUser.user_type = #{userType}
 		  
 		 
 			 and systemUser.state = #{state}
 		  
 		 
 			 and systemUser.creator_id = #{creatorId}
 		  
 		 
 			 and systemUser.create_time = #{createTime}
 		  
 		 
 			 and systemUser.updator_id = #{updatorId}
 		  
 		 
 			 and systemUser.update_time = #{updateTime}
 		  
 		 
 			 and systemUser.directly_org_id = #{directlyOrgId}
 		  
 		 
 			 and systemUser.hierarchy_org_id = #{hierarchyOrgId}
 		  
 		 
 			 and systemUser.certificates_type = #{certificatesType}
 		  
 		 
 			 and systemUser.certificates_num = #{certificatesNum}

		 select 
 		  count(systemUser.id)
 		  from system_user_tb systemUser
 		  where 1=1 
 		 
 			 and systemUser.id = #{id}
 		  
 		 
 			 and systemUser.login_name = #{loginName}
 		  
 		 
 			 and systemUser.user_name = #{userName}
 		  
 		 
 			 and systemUser.password = #{password}
 		  
 		 
