 			 and systemUser.user_type = #{userType}
 		  
 		 
 			 and systemUser.state = #{state}
 		  
 		 
 			 and systemUser.creator_id = #{creatorId}
 		  
 		 
 			 and systemUser.create_time = #{createTime}
 		  
 		 
 			 and systemUser.updator_id = #{updatorId}
 		  
 		 
 			 and systemUser.update_time = #{updateTime}
 		  
 		 
 			 and systemUser.directly_org_id = #{directlyOrgId}
 		  
 		 
 			 and systemUser.hierarchy_org_id = #{hierarchyOrgId}
 		  
 		 
